    const preprocessor = new Test-local-providerPreprocessor();

    // Test preprocessing functionality
    const testRequest = {
        method: 'POST',
        url: 'https://api.example.com/test',
        headers: {},
        body: { message: 'Hello, world!' }
    };

    const processed = await preprocessor.preprocessRequest(testRequest);

    console.assert(processed.preprocessedBy === 'test-local-provider');
    console.assert(processed.headers['X-Provider'] === 'test-local-provider');

